class HealthTracker {
    boolean active;
    int heartRate;
    int steps;
    int sleepHours;

    public HealthTracker(boolean active, int heartRate, int steps, int sleepHours) {
        this.active = active;
        this.heartRate = heartRate;
        this.steps = steps;
        this.sleepHours = sleepHours;
    }

    public boolean isAtRisk() {
        if (!active && (heartRate > 100 || sleepHours < 7)) return true;
        return false;
    }

    public void exercise() {
        steps += 5000;
        heartRate += 20;
    }

    public void sleep(int hours) {
        sleepHours += hours;
        if (sleepHours >= 8) heartRate -= 10;
    }
}

