class Product:
    def __init__(self, price, on_sale):
        self.price = price
        self.on_sale = on_sale
        self.discount_rate = 0.1 if on_sale else 0.0

    def apply_discount(self):
        if self.on_sale:
            self.price *= (1 - self.discount_rate)

class Customer:
    def __init__(self, budget):
        self.budget = budget

    def can_purchase(self, product):
