class RoboticsSystem {
  int batteryLevel;
  boolean isFunctional;

  public RoboticsSystem(int initialBatteryLevel) {
    batteryLevel = initialBatteryLevel;
    isFunctional = batteryLevel > 20;
  }

  public void chargeBattery(int increment) {
    batteryLevel += increment;
    if (batteryLevel > 100) batteryLevel = 100;
    isFunctional = batteryLevel > 20;
  }

  public void useRobot(int decrement) {
    if (isFunctional) {
      batteryLevel -= decrement;
      if (batteryLevel <= 20) {
        isFunctional = false;
        batteryLevel = 0;
      }
    }
  }

  public boolean checkIfFunctional() {
    return isFunctional;
  }
}
