import random

# Customer class
class Customer:
    def __init__(self, age):
        self.age = age
        self.is_happy = None

    def receive_service(self, service_quality):
        if service_quality > 7:
            self.is_happy = True
        elif service_quality < 4:
            self.is_happy = False
        else:
            self.is_happy = None

# Business logic
def serve_customer(customer_age):
    random_quality = random.randint(1, 10)
    c = Customer(customer_age)
    c.receive_service(random_quality)
    return c.is_happy
