class ShoppingCart:
    def __init__(self):
        self.items = []

    def add_item(self, item_price):
        self.items.append(item_price)

    def calculate_total(self):
        if not self.items:
            return 0
        discount = 0.1 if sum(self.items) > 100 else 0
        total = sum(self.items) - (sum(self.items) * discount)
        return total

    def apply_discount_code(self, discount_code):
        if discount_code == 'SAVE20':
            self.items = [price * 0.8 for price in self.items]

