class HealthSystem:
    def __init__(self, num_hospitals):
        self.num_hospitals = num_hospitals
        self.patient_data = {}

    def admit_patient(self, patient_id, condition):
        self.patient_data[patient_id] = condition
        self.update_resources(patient_id, condition)

    def update_resources(self, patient_id, condition):
        if condition == 'critical':
            self.num_hospitals -= 1

    def discharge_patient(self, patient_id):
        if self.patient_data.get(patient_id) == 'critical':
            self.num_hospitals += 1
        del self.patient_data[patient_id]

system = HealthSystem(5)
system.admit_patient('001', 'critical')
system.discharge_patient('001')
