class OnlineStore:
    def __init__(self, product_list):
        self.products = {product['id']: product for product in product_list}
        self.sales = {product_id: 0 for product_id in self.products}

    def add_sale(self, product_id, amount):
        if product_id in self.products and amount > 0:
            self.sales[product_id] += amount
            self.products[product_id]['stock'] -= amount
            return True
        return False

    def check_stock(self, product_id):
        if product_id in self.products:
            return self.products[product_id]['stock']
        return None

# Example usage:
products = [
