  class HealthMonitor {
    private int calorieIntake;
    private int waterConsumed; // in ml
    private int sleepHours;
    private boolean feelingWell;

    public HealthMonitor(int calorieIntake, int waterConsumed, int sleepHours) {
      this.calorieIntake = calorieIntake;
      this.waterConsumed = waterConsumed;
      this.sleepHours = sleepHours;
      this.feelingWell = evaluateHealth();
    }

    private boolean evaluateHealth() {
      boolean goodSleep = sleepHours >= 8;
      boolean goodHydration = waterConsumed >= 2000;
      boolean goodDiet = calorieIntake >= 1200 && calorieIntake <= 2500;
      return goodSleep && goodHydration && goodDiet;
    }

    public void drinkWater(int amount) {
      this.waterConsumed += amount;
      this.feelingWell = evaluateHealth();
    }

    public void changeDiet(int calories) {
      this.calorieIntake = calories;
      this.feelingWell = evaluateHealth();
    }

    public void recordSleep(int hours) {
      this.sleepHours = hours;
      this.feelingWell = evaluateHealth();
    }

    public boolean isFeelingWell() {
      return feelingWell;
    }
  }
