class ProductMarketing:
    def __init__(self, price, ad_budget, competitor_price):
        self.price = price
        self.ad_budget = ad_budget
        self.competitor_price = competitor_price
        self.sales = 0

    def calculate_sales(self):
        if self.price < self.competitor_price:
            self.sales = 100 + 3 * self.ad_budget
        else:
            self.sales = 50 + 2 * self.ad_budget

    def change_price(self, new_price):
        self.price = new_price
        self.calculate_sales()

    def increase_ad_budget(self, increment):
        self.ad_budget += increment
        self.calculate_sales()",

