import random

def initialize_population(size):
  return [{'health': 'good' if random.random() > 0.2 else 'poor', 'employed': random.choice([True, False])} for _ in range(size)]

def offer_job(person):
  if person['health'] == 'good':
    person['employed'] = True

def health_program(person):
  if not person['employed']:
    person['health'] = 'good'

population = initialize_population(100)
for individual in population:
  if random.random() > 0.8:
    health_program(individual)
  offer_job(individual)
