import random

# Define potential customers' likelihood to purchase
CUSTOMER_BASE = {'low_income': 0.2, 'middle_income': 0.6, 'high_income': 0.8}

# Marketing strategy that can be applied
def apply_marketing_strategy(customer_group, strategy):
    if strategy == 'discount':
        return CUSTOMER_BASE[customer_group] + 0.1
    elif strategy == 'premium_ad':
        return CUSTOMER_BASE[customer_group] * 1.2
    else:
        return CUSTOMER_BASE[customer_group]

# Simulating customer decision to purchase
def simulate_purchase_decision(likelihood):
    return random.random() < likelihood

# Simulation of sales day
def sales_day(customers, strategy):
    sales = 0
    for customer in customers:
        likelihood = apply_marketing_strategy(customer, strategy)
        if simulate_purchase_decision(likelihood):
            sales += 1
    return sales
