class SalesCalculator {
    public int calculateDiscount(int product_price, int season) {
        int discount;
        if (season == 1) { 
            discount = product_price * 20 / 100;
        } else if (season == 2) {
            discount = product_price * 10 / 100;
        } else if (season == 3) {
            discount = product_price * 15 / 100;
        } else {
            discount = product_price * 5 / 100;
        }
        return discount;
    }

    public int calculateFinalPrice(int product_price, int discount) {
        return product_price - discount;
    }
}

SalesCalculator calc = new SalesCalculator();
int season = 1; // Assume it's winter
int price = 500;
int discount = calc.calculateDiscount(price, season);
int final_price = calc.calculateFinalPrice(price, discount);",

