import random

class Weather:
    def __init__(self, temperature):
        self.temperature = temperature
        self.is_raining = random.choice([True, False])

class Mood:
    def __init__(self, weather):
        self.happy = True if not weather.is_raining and weather.temperature > 20 else False

    def go_out(self):
        if self.happy:
            return 'Going out for a walk!'
        else:
            return 'Staying in today.'

def main():
    today_weather = Weather(25)
    my_mood = Mood(today_weather)
    print(my_mood.go_out())

if __name__ == '__main__':
    main()
