import numpy as np

class TradingSystem:
    def __init__(self, initial_balance):
        self.balance = initial_balance
        self.owned_stocks = 0

    def buy_stock(self, price_per_stock, num_stocks):
        total_cost = price_per_stock * num_stocks
        if self.balance >= total_cost:
            self.balance -= total_cost
            self.owned_stocks += num_stocks

    def sell_stock(self, price_per_stock, num_stocks):
        if self.owned_stocks >= num_stocks:
            self.balance += price_per_stock * num_stocks
            self.owned_stocks -= num_stocks

# Simulation of daily trading
trading_system = TradingSystem(1000)
prices = [20, 22, 18, 21]
for price in prices:
    if price < 21:
        trading_system.buy_stock(price, 10)
