  import random

  def influence_weather():
      weather = random.choice(['sunny', 'rainy', 'cloudy'])
      if weather == 'sunny':
          mood = 'happy'
      elif weather == 'rainy':
          mood = 'sad'
      else:
          mood = 'neutral'
