  import random

  # Experiment setup
  weather_conditions = ['sunny', 'rainy', 'cloudy', 'foggy']
  mood_levels = ['happy', 'neutral', 'sad']

  # Weather affects mood
  def determine_mood(weather):
      if weather == 'sunny':
          return random.choice(['happy', 'neutral'])
      elif weather == 'rainy':
          return random.choice(['sad', 'neutral'])
      elif weather == 'cloudy':
          return 'neutral'
      else:
          return random.choice(mood_levels)

  # Mood affects productivity
  def productivity_level(mood):
      if mood == 'happy':
          return random.choice(['high', 'medium'])
      elif mood == 'sad':
          return 'low'
      else:
          return random.choice(['medium', 'low'])

  # Simulating a day
  current_weather = random.choice(weather_conditions)
  current_mood = determine_mood(current_weather)
  current_productivity = productivity_level(current_mood)

  print(f"Weather: {current_weather}, Mood: {current_mood}, Productivity: {current_productivity}")
