# Simulating a traffic management system

import random

def check_weather():
    weather = random.choice(['rainy', 'sunny', 'cloudy'])
    return weather

def calculate_traffic_density(weather):
    if weather == 'rainy':
        return random.randint(70, 100)  # Higher density
    elif weather == 'sunny':
        return random.randint(20, 50)   # Lower density
    else:
        return random.randint(40, 70)   # Medium density

def deploy_traffic_lights(density):
    if density > 60:
        return 'red'
    else:
        return 'green'

def apply_emergency_brakes(signal):
    if signal == 'red':
        return True
    else:
        return False

# Main function to run simulation
weather = check_weather()
density = calculate_traffic_density(weather)
signal = deploy_traffic_lights(density)
brakes = apply_emergency_brakes(signal)

print(f"Weather: {weather}, Traffic Density: {density}, Traffic Light: {signal}, Brakes Applied: {brakes}")
""",

