class WeatherSimulator {
    private boolean sunny = false;
    private double temperature = 20.0; // Temperature in Celsius

    public void setSunny(boolean sunny) {
        this.sunny = sunny;
        if (sunny) this.temperature += 5;
        else this.temperature -= 5;
    }

    public boolean isSunny() {
        return sunny;
    }

    public double getTemperature() {
        return temperature;
    }

    public void increaseGlobalWarmingEffect() {
        temperature += 3;
    }
}

