class LoanApplication:
    def __init__(self, credit_score, income, requested_loan, employment, savings):
        self.credit_score = credit_score
        self.income = income
        self.requested_loan = requested_loan
        self.employment = employment
        self.savings = savings

    def approve_loan(self):
        base_approval = self.credit_score > 700 and self.income > 50000
        if self.employment == 'unemployed':
            return False
        elif self.savings > 5000:
            return base_approval or (self.requested_loan < 10000)
        else:
            return base_approval and (self.requested_loan <= self.savings * 0.5)

