# Python code to demonstrate a simple reward system based on customer behavior
class Customer:
    def __init__(self, age, is_student, emails_clicked, purchases):
        self.age = age
        self.is_student = is_student
        self.emails_clicked = emails_clicked
        self.purchases = purchases

    def check_discount_eligibility(self):
        # Base discount eligibility checking age and student status
        discount = 10 if self.age < 30 or self.is_student else 5
        if self.emails_clicked > 5:
            discount += 5  # additional discount for engaged users
        if self.purchases > 3:
            discount += 10  # additional discount for frequent buyers
        return discount

def send_discount_code(customer):
    discount = customer.check_discount_eligibility()
    if discount > 20:
        print("Send 20% discount code")
    else:
        print(f"Send {discount}% discount code")
