class ShoppingCart {
    int itemCount;
    double totalPrice;

    ShoppingCart() {
        this.itemCount = 0;
        this.totalPrice = 0.0;
    }

    void addItem(double price) {
        this.itemCount++;
        this.totalPrice += price;
    }

    void applyDiscount(double discountRate) {
        this.totalPrice *= (1 - discountRate);
    }

    void removeItem(double price) {
        if (this.itemCount > 0) {
            this.itemCount--;
            this.totalPrice -= price;
        }
    }
}",

