import pandas as pd
import numpy as np

# Generating synthetic data
np.random.seed(0)
data = pd.DataFrame({
    'weather': np.random.choice(['sunny', 'rainy', 'cloudy'], size=100),
    'sales': np.zeros(100)
})

# Simulating sales based on weather
def simulate_sales(row):
    if row['weather'] == 'sunny':
        return np.random.normal(200, 30)
    elif row['weather'] == 'rainy':
        return np.random.normal(100, 20)
    else:
        return np.random.normal(150, 25)

data['sales'] = data.apply(simulate_sales, axis=1)

# Intervention: improving sales on rainy days
def improve_sales_on_rainy_days(row):
    if row['weather'] == 'rainy':
        return row['sales'] + 50
    return row['sales']

data['sales'] = data.apply(improve_sales_on_rainy_days, axis=1)
  """,

