class School:
    def __init__(self, name):
        self.name = name
        self.students = []

    def enroll(self, student):
        self.students.append(student)
        student.school = self

class Student:
    def __init__(self, name, grades=None):
        self.name = name
        self.grades = grades or {}
        self.school = None

    def assign_grade(self, course, grade):
        self.grades[course] = grade

    def transfer(self, new_school):
        if self.school:
            self.school.students.remove(self)
        new_school.enroll(self)

# Usage
