  import random

  class WeatherSimulator:
      def __init__(self):
          self.temperature = 20
          self.humidity = 80

      def update_weather(self):
          change = random.choice([-1, 1])
          self.temperature += change
          if self.temperature > 25:
              self.humidity -= 5
          else:
              self.humidity += 3

  class MoodDetector:
      def __init__(self, temperature):
          self.mood = 'Neutral'
          if temperature > 23:
              self.mood = 'Happy'
          elif temperature < 18:
              self.mood = 'Sad'

      def check_mood_change(self, humidity):
          if humidity < 50:
              self.mood = 'Irritated'
          elif humidity > 70:
              self.mood = 'Calm'

  def main():
      weather_station = WeatherSimulator()
      for _ in range(10):
          weather_station.update_weather()
          mood_detector = MoodDetector(weather_station.temperature)
          mood_detector.check_mood_change(weather_station.humidity)
          print(f"Temperature: {weather_station.temperature}, Humidity: {weather_station.humidity}, Mood: {mood_detector.mood}")

  if __name__ == '__main__':
      main()
