class HealthMonitor:
    def __init__(self, age, weight, height):
        self.age = age
        self.weight = weight
        self.height = height
        self.bmi = self.calculate_bmi()

    def calculate_bmi(self):
        return self.weight / ((self.height / 100) ** 2)

    def health_risk(self):
        if self.bmi < 18.5:
            return 'Low'
        elif 18.5 <= self.bmi < 25:
            return 'Moderate'
        else:
            return 'High'

patient = HealthMonitor(30, 70, 175)
risk_level = patient.health_risk()",

