class EconomySystem {
    double inflation; // Higher inflation usually means lower purchasing power.
    double interestRate; // Central bank interest rates affect inflation.
    double unemployment; // General unemployment rate in the economy.

    EconomySystem(double initialRate) {
        inflation = 2.0; // Base inflation rate in percent.
        interestRate = initialRate; // Set initial interest rate.
        this.updateUnemployment();
    }

    void updateUnemployment() {
        unemployment = (10 - interestRate * 0.5) * (1 + inflation / 100);
    }

    void increaseInterestRate(double increment) {
        interestRate += increment;
        updateUnemployment();
    }
