class TrafficSystem:
    def __init__(self, cars=0, traffic_lights=False):
        self.cars = cars
        self.traffic_lights = traffic_lights

    def increase_traffic(self):
        self.cars += 1
        if self.cars > 10 and not self.traffic_lights:
            self.traffic_lights = True

    def decrease_traffic(self):
        if self.cars > 0:
            self.cars -= 1
        if self.cars <= 10:
            self.traffic_lights = False

    def install_traffic_lights(self):
        self.traffic_lights = True

    def remove_traffic_lights(self):
        if self.cars <= 10:
            self.traffic_lights = False
