class SalesAnalysis:
    def __init__(self, sales_data):
        self.sales_data = sales_data

    def average_price(self):
        total = sum(item['price'] * item['units'] for item in self.sales_data)
        count = sum(item['units'] for item in self.sales_data)
        return total / count if count else 0

    def update_sales(self, product_id, units_sold):
        for item in self.sales_data:
            if item['product_id'] == product_id:
                item['units'] += units_sold
                break

    def predict_future_sales(self, month):
        current_units = sum(item['units'] for item in self.sales_data if item['month'] == month)
        return current_units * 1.1  # projected 10% increase

