class Store {
  constructor() {
    this.stock = {'apple': 20, 'banana': 30};
    this.prices = {'apple': 1.0, 'banana': 0.5};
  }

  purchase(item, quantity) {
    if(this.stock[item] >= quantity) {
      this.stock[item] -= quantity;
      return this.prices[item] * quantity;
    }
    return 0;
  }

  restock(item, quantity, price) {
    if(item in this.stock) {
      this.stock[item] += quantity;
      if(price !== undefined) {
        this.prices[item] = price;
      }
    }
  }
}",

