        self.historical_sales = historical_sales
        self.marketing_budget = marketing_budget

    def predict_sales(self, marketing_boost):
        return self.historical_sales * (1 + 0.05 * self.marketing_budget + 0.1 * marketing_boost)

    def set_marketing_budget(self, new_budget):
        self.marketing_budget = new_budget
        print(f'Updated marketing budget to {self.marketing_budget}')",

