      class Simulation {
          public int initialPopulation;
          public double growthRate;

          public Simulation(int population, double rate) {
              initialPopulation = population;
              growthRate = rate;
          }

          public int simulateYear() {
              initialPopulation = (int)(initialPopulation * (1 + growthRate));
              return initialPopulation;
          }

          public void implementPolicyChange(double effect) {
              growthRate += effect;
          }
      }

      public static void main(String[] args) {
          Simulation sim = new Simulation(100, 0.1); // Initial population of 100 and growth rate of 10%
          int populationNextYear = sim.simulateYear(); // Simulates a year
          sim.implementPolicyChange(0.05); // Policy increases the growth rate by 5%
          int populationAfterPolicy = sim.simulateYear(); // Simulates another year after policy change
      }
