  # A sample Python program to demonstrate causal relationships in a bike store

  import random

  class BikeStore:
      def __init__(self):
          self.number_of_bikes = 100
          self.customer_satisfaction = 90  # out of 100

      def sell_bike(self, number_sold):
          if number_sold >= self.number_of_bikes:
              self.number_of_bikes = 0
          else:
              self.number_of_bikes -= number_sold

          self.adjust_satisfaction(number_sold)

      def receive_shipment(self, number_received):
          self.number_of_bikes += number_received
          if number_received > 50:
              self.improve_satisfaction(5)

      def adjust_satisfaction(self, number):
          if number > 5:
              self.customer_satisfaction -= 2
          else:
              self.customer_satisfaction -= 1

      def improve_satisfaction(self, increment):
          self.customer_satisfaction += increment
          if self.customer_satisfaction > 100:
              self.customer_satisfaction = 100

  store = BikeStore()
  store.sell_bike(3)
  store.sell_bike(7)
  store.receive_shipment(55)
