class ProductSalesPredictor:
    def __init__(self, base_sales, season_factor, advertisement_spending):
        self.base_sales = base_sales
        self.season_factor = season_factor
        self.advertisement_spending = advertisement_spending

    def predict_sales(self):
        seasonal_sales = self.base_sales * self.season_factor
        influenced_sales = 0.1 * self.advertisement_spending
        return seasonal_sales + influenced_sales

    def update_advertising(self, new_spending):
        self.advertisement_spending = new_spending
        return self.predict_sales()
