class WeatherForecast {
  private boolean isRainy;
  private int temperature;

  public WeatherForecast(boolean rain, int temp) {
    this.isRainy = rain;
    this.temperature = temp;
  }

  public void updateWeather(boolean rain, int temp) {
    if (rain) {
      this.isRainy = true;
      this.temperature -= 5; // temperature drops when it rains
    } else {
      this.isRainy = false;
      if (this.temperature < 20) {
        this.temperature += 3;  // increases slightly on dry days if below 20 degrees
      }
    }
  }

  public boolean getRainStatus() {
    return isRainy;
  }

  public int getTemperature() {
    return temperature;
  }
}",

