class ClimateController:
    def __init__(self, temperature, humidity):
        self.temperature = temperature
        self.humidity = humidity

    def adjust_temperature(self, adjustment):
        self.temperature += adjustment
        if self.temperature > 25:
            self.activate_cooling_system()

    def activate_cooling_system(self):
        self.humidity -= 5
        if self.humidity < 30:
            self.activate_humidifier()

    def activate_humidifier(self):
        self.humidity += 10

climate_ctrl = ClimateController(22, 40)
climate_ctrl.adjust_temperature(5)
