def calculate_performance(metrics, adjustment_factor=1.0):
    accuracy = metrics['accuracy'] * adjustment_factor
    precision = metrics['precision'] * adjustment_factor
    recall = metrics['recall'] * adjustment_factor
    if precision + recall == 0:
        f1_score = 0
    else:
        f1_score = 2 * (precision * recall) / (precision + recall)
    return {'accuracy': accuracy, 'precision': precision, 'recall': recall, 'f1_score': f1_score}

metrics = {'accuracy': 0.9, 'precision': 0.8, 'recall': 0.85}
ad_factor = 1.1
calculated_metrics = calculate_performance(metrics, ad_factor)",

