    if (newTemperature > 30) {
      isRaining = false;
    } else if (newTemperature < 10) {
      isRaining = true;
    }
    temperature = newTemperature;
  }

  public String getWeatherStatus() {
    return "Raining: " + isRaining + ", Temperature: " + temperature;
  }
