# Python program to simulate an online store's sales system
import random

class Product:
    def __init__(self, name, price):
        self.name = name
        self.price = price

class Inventory:
    def __init__(self):
        self.stock = {}

    def add_product(self, product, quantity):
        if product.name in self.stock:
            self.stock[product.name] += quantity
        else:
            self.stock[product.name] = quantity

    def remove_product(self, product, quantity):
        if product.name in self.stock and self.stock[product.name] >= quantity:
            self.stock[product.name] -= quantity
            return True
        return False

class Discount:
    def apply_discount(self, price, discount_percentage):
        return price - (price * (discount_percentage / 100))

class Sales:
    def __init__(self, inventory):
        self.inventory = inventory

    def sell_product(self, product, quantity, discount=0):
        if self.inventory.remove_product(product, quantity):
            full_price = product.price * quantity
            final_price = self.apply_discount(full_price, discount) if discount > 0 else full_price
            return final_price
        else:
            return None

    def apply_discount(self, price, discount_percentage):
        discount_obj = Discount()
        return discount_obj.apply_discount(price, discount_percentage)

# Initialize objects
