class Inventory:
    def __init__(self):
        self.stock = {}

    def add_product(self, product, quantity):
        if product.name in self.stock:
            self.stock[product.name] += quantity
        else:
            self.stock[product.name] = quantity

    def remove_product(self, product, quantity):
        if product.name in self.stock and self.stock[product.name] >= quantity:
            self.stock[product.name] -= quantity
            return True
        return False

class Discount:
    def apply_discount(self, price, discount_percentage):
        return price - (price * (discount_percentage / 100))

class Sales:
