class WeatherStation:

    def __init__(self, temperature):
        self.temperature = temperature
        self.weather = None
        self.adjusted_temperature = None

    def determine_weather(self):
        if self.temperature <= 32:
            self.weather = 'freezing'
        elif self.temperature <= 50:
            self.weather = 'cold'
        elif self.temperature <= 75:
            self.weather = 'mild'
        else:
            self.weather = 'hot'

    def adjust_temperature(self, adjustment):
        self.adjusted_temperature = self.temperature + adjustment
        self.determine_weather()

station = WeatherStation(70)
station.determine_weather()
station.adjust_temperature(10)
