class WeatherSimulator {
  int temperature;
  boolean isRaining;

  public WeatherSimulator() {
    temperature = 20; // Default temperature in Celsius
    isRaining = false; // Default weather condition
  }

  public void setTemperature(int newTemp) {
    temperature = newTemp;
    if (newTemp < 0) {
      isRaining = false; // It's too cold to rain, assuming it snows instead
    }
  }

  public void startRain() {
    if (temperature > 0) {
      isRaining = true; // It can rain if above freezing
    }
  }

  public boolean isWeatherCold() {
    return temperature < 5;
  }

  public boolean willItSnow() {
    return isWeatherCold() && !isRaining;
  }
}",

