class WeatherForecast: 
    def __init__(self, temperature):
        self.temperature = temperature
        self.is_rainy = self.determine_weather()

    def determine_weather(self):
        if self.temperature < 10:
            return True  # It’s likely to rain when it's cold
        return False

    def will_carry_umbrella(self, has_umbrella):
        if self.is_rainy and has_umbrella:
            return True
        return False 

