class SalesData {
    int totalSales, newSubscriptions;
    double pricePerSubscription;

    public SalesData(int newSubscribers, double price) {
        this.newSubscriptions = newSubscribers;
        this.pricePerSubscription = price;
        updateSales();
    }

    public void updatePrice(double newPrice) {
        this.pricePerSubscription = newPrice;
        updateSales();
    }

    public void addSubscriptions(int additionalSubs) {
        this.newSubscriptions += additionalSubs;
        updateSales();
    }

    private void updateSales() {
        totalSales = (int) (newSubscriptions * pricePerSubscription);
    }

    public int getTotalSales() {
        return totalSales;
    }
}

