class CovidSpreadSimulation: 
    def __init__(self, population, rate): 
        self.population = population 
        self.rate = rate 
        self.infected = 1 

    def spread_infection(self, days): 
        for day in range(days): 
            new_infections = self.infected * self.rate 
            self.infected += new_infections 
            if self.infected > self.population: 
                self.infected = self.population 

    def introduce_vaccine(self, efficiency): 
        if self.infected > 0: 
            reduction = self.infected * efficiency 
            self.infected -= reduction 
            self.infected = max(self.infected, 0) 
