class Experiment:
    def __init__(self, init_temp):
        self.temperature = init_temp
        self.system_status = 'stable'

    def increase_temperature(self, increment):
        if increment > 0:
            self.temperature += increment
            if self.temperature > 100:
                self.system_status = 'danger'
            elif self.temperature > 50:
                self.system_status = 'warning'

    def decrease_temperature(self, decrement):
        if decrement > 0:
            self.temperature -= decrement
            if self.temperature < 10:
                self.system_status = 'critical'
            elif self.temperature < 30:
                self.system_status = 'caution'

