class Environment:
    def __init__(self, sunlight, water):
        self.sunlight = sunlight
        self.water = water
        self.fertilizer = 0

    def add_fertilizer(self, amount):
        self.fertilizer += amount

class Plant:
    def __init__(self, species):
        self.species = species
        self.health = 50

    def grow(self, environment):
        if environment.sunlight > 5 and environment.water > 20:
            self.health += 20
        if environment.fertilizer > 0:
            self.health += 10 * (environment.fertilizer / 5)

def simulate_growth(species, sunlight, water, fertilize=False):
    env = Environment(sunlight, water)
    if fertilize:
        env.add_fertilizer(5)
    plant = Plant(species)
    plant.grow(env)
    return plant.health
