class WeatherSimulator:
    def __init__(self, temperature):
        self.temperature = temperature
        self.cloudy = False
        self.rainy = False

    def update_weather(self):
        if self.temperature < 20:
            self.cloudy = True
        if self.cloudy and self.temperature < 15:
            self.rainy = True

simulator = WeatherSimulator(10)
simulator.update_weather()
print(simulator.cloudy, simulator.rainy)",

