class TrafficSystem:
    def __init__(self, light_status='red'):
        self.light_status = light_status
        self.car_count = 0

    def toggle_traffic_light(self):
        if self.light_status == 'red':
            self.light_status = 'green'
        else:
            self.light_status = 'red'

    def car_arrives(self):
        if self.light_status == 'green':
            self.car_count += 1

    def car_departs(self):
        if self.light_status == 'green' and self.car_count > 0:
            self.car_count -= 1

