class WeatherSystem {
  private int temperature = 20;  // in degrees Celsius
  private boolean isRaining = false;

  public WeatherSystem(boolean startsRaining) {
    setWeather(startsRaining);
  }

  private void setWeather(boolean rainStatus) {
    if (rainStatus) {
      isRaining = true;
      temperature -= 5;
    }
  }

  public boolean checkRain() {
    return isRaining;
  }

  public int getTemperature() {
    return temperature;
  }

  public void changeTemperature(int change) {
    temperature += change;
    if (temperature <= 15) {
      isRaining = true;
    }
  }
}",

