    if (isRaining) {
      return true;
    }
    return false;
  }

  public boolean isColdWeather() {
    if (temperature < 5) {
      return true;
    }
    return false;
  }

  public void startRain() {
    this.isRaining = true;
  }

  public void decreaseTemperature(int decrement) {
    this.temperature -= decrement;
  }
